import React, { useEffect, useState} from "react";
import "./weather.css"
import { suggestion } from "./suggestion";
import axios from "axios";

const WeatherM =() =>
{
    const [crntCity, setCrntCity] = useState("");
    const [fetchCityName, fetchCrntCityName] = useState("");
    const [name , setName] = useState("tokyo");
    const [crntTemp , setCrntTemp] = useState([]);
    const [suggarr , setSuggArr] =  useState([]);
    const [latitude , setLatitude] = useState("");
    const [longitude , setLongitude] = useState("");
    navigator.geolocation.getCurrentPosition((position) =>{
        setLatitude(position.coords.latitude);
        setLongitude(position.coords.longitude);
    })
    useEffect(async ()=>{
        const API = await fetch("https://api.openweathermap.org/data/2.5/weather?lat="+ latitude + "&lon="+ longitude +"&appid=9e4cdd6c628c4cbdcacc25b6df594009");
        const API_result = await API.json();
        setCrntCity(API_result);
    },[longitude,latitude])
    useEffect(async ()=>{
        const res = await fetch("https://api.openweathermap.org/data/2.5/weather?q=" +  name   +"&units=metric&appid=9e4cdd6c628c4cbdcacc25b6df594009"); 
        const result = await res.json();
        setCrntTemp(result.main);
        fetchCrntCityName(result.name);
    },[name])
    const doChange = (event) =>
    {
        const {value} = event.target;
        setCrntCity(value);
        if(value !== ""){
            suggestion(value).then((response)=>{
                setSuggArr(response);
            });
        }else setSuggArr([]);
    }
    const searchData = () =>{
        setName(crntCity);
    }
    var ind = 0;
    return(
        <>
            <div className="Body_wrapper">
                <div className="card_wrapper">
                    <div className="card">
                        <div className="card_header">
                            <div className="search_bar">
                                <input 
                                    type="search"
                                    placeholder="Enter City Name"
                                    onChange={doChange}
                                    value = {crntCity}
                                />
                                <button onClick= {searchData}>search</button>
                                <div className="suggestion_section">
                                {suggarr.map((elem , index) => {
                                        if(ind<4){
                                            ind ++;
                                            return(
                                            <h1 key={index} className="suggestions" onClick={() =>{
                                            setCrntCity(elem.city);                                
                                            setSuggArr([]);}}> 
                                            {elem.city} , {elem.state} 
                                            </h1>
                                            )
                                        }
                                        return ;
                                    })}
                                </div>
                            </div>
                        </div>
                        {!crntTemp? (
                            <p className="data-exception">No data Found</p>
                        ):(
                            <>
                            <div className="card_main">
                            <div className="city_info">
                                <div className="cities"> 
                                    <i className="fa-solid fa-location-dot"></i>
                                    <h1 className="fetchcity"> {fetchCityName} </h1></div>
                                <div className="temp"><h1> {crntTemp.temp} °Cel </h1></div>
                            </div>
                            <div className="min-max_temp">
                                <h3> min-temp : {crntTemp.temp_min} °Cel</h3>
                                <h3>||</h3>
                                <h3>max-temp :{crntTemp.temp_max} °Cel</h3>
                            </div>
                            </div>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </>
    )
}

export default WeatherM

