import React, { useEffect, useState } from "react";
import "./weather.css"
const WeatherM =() =>
{
    const [crntCity, setCrntCity] = useState(null);
    const [name , setName] = useState("pune");
    const [crntTemp , setCrntTemp] = useState(null);
    useEffect(async ()=>{
        const res = await fetch("https://api.openweathermap.org/data/2.5/weather?q=" +  name   +"&appid=9e4cdd6c628c4cbdcacc25b6df594009"); 
        const result = await res.json();
        setCrntTemp(result.main);
    },[])
    const doChange = (event) =>
    {
        setCrntCity(event.target.value);
    }
    return(
        <>
            <div className="Body_wrapper">
                <div className="card_wrapper">
                    <div className="card">
                        <div className="card_header">
                            <div className="search_bar">
                                <input 
                                    type="search"
                                    placeholder="Enter City Name"
                                    onChange={doChange}
                                    value = {crntCity}
                                />
                                {/*flag && <div className="suggestion_section"></div>*/}
                                <button onClick= {() => setName()}>search</button>
                            </div>
                        </div>
                        <div className="card_main">
                            <div className="city_info">
                                <div className="cities"> 
                                    <i className="fa-solid fa-location-dot"></i>
                                    <h1> {name} </h1></div>
                                <div className="temp"><h1> {crntTemp.temp} </h1></div>
                            </div>
                            <div className="min-max_temp">
                                <h3> min-temp : {crntTemp.temp_min}</h3>
                                <h3>||</h3>
                                <h3>max-temp :{crntTemp.temp_max}</h3>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default WeatherM

