import React, { useState } from "react";
import "./weather.css"
const WeatherM =() =>
{
    const [city, crntCity] = useState();
    const [flag , setFlag] = useState(false);
    const [name , setName] = useState();
    const doChange = (event) =>
    {
        crntCity(event.target.value);
    }
    return(
        <>
            <div className="Body_wrapper">
                <div className="card_wrapper">
                    <div className="card">
                        <div className="card_header">
                            <div className="search_bar">
                                <input 
                                    type="search"
                                    placeholder="Enter City Name"
                                    onChange={doChange}
                                    value = {city}
                                />
                                {flag && <div className="suggestion_section"></div>}
                                <button onClick= {() => setName(city)}>search</button>
                            </div>
                        </div>
                        <div className="card_main">
                            <div className="city_info">
                                <div className="cities"> 
                                    <i class="fa-solid fa-location-dot"></i>
                                    <h1> {name} </h1></div>
                                <div className="temp"><h1> 231 </h1></div>
                            </div>
                            <div className="min-max_temp">
                                <h3> min-temp :</h3>
                                <h3>||</h3>
                                <h3>max-temp :</h3>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default WeatherM

