import axios from "axios";
import React, { useEffect, useState } from "react";

const Location = () =>
{
    const [latitude , setLatitude] = useState("");
    const [longitude , setLongitude] = useState("");
    const [crntCity , setCrntCity] = useState("");
    navigator.geolocation.getCurrentPosition((position) =>{
        setLatitude(position.coords.latitude);
        setLongitude(position.coords.longitude);
    })
    const API = "https://api.openweathermap.org/data/2.5/weather?lat="+ latitude + "&lon="+ longitude+"&appid=9e4cdd6c628c4cbdcacc25b6df594009"
    useEffect(()=>{
        axios.get(API).then((response) => setCrntCity(response.data.name));
    },[latitude,longitude])
    return crntCity;
}

export {Location};
