import axios from "axios";
import { useEffect, useState } from "react";

export const Location = () =>
{
    const [data , setData] = useState(null)
    useEffect(()=>{
        navigator.geolocation.getCurrentPosition((position) =>{
            setData(position);
        })
    })
    return data;
}
