import axios from "axios";
import { useEffect, useState } from "react";

export const Location = (lat , long ) =>
{
    const [crntCity , setCrntCity] = useState("");
    const API = "https://api.openweathermap.org/data/2.5/weather?lat="+ lat + "&lon="+ long +"&appid=9e4cdd6c628c4cbdcacc25b6df594009"
    axios.get(API).then((response) => {
        setCrntCity(response.data.name);
    });
    return crntCity;
}
