import React, { useEffect, useState } from "react";
import "./weather.css"
const WeatherM =() =>
{
    const [crntCity, setCrntCity] = useState("");
    const [fetchCityName, fetchCrntCityName] = useState("");
    const [name , setName] = useState("tokyo");
    const [crntTemp , setCrntTemp] = useState([]);
    const [flag , setFlag ] = useState(true);
    useEffect(async ()=>{
        const res = await fetch("https://api.openweathermap.org/data/2.5/weather?q=" +  name   +"&units=metric&appid=9e4cdd6c628c4cbdcacc25b6df594009"); 
        const result = await res.json();
        setCrntTemp(result.main);
        fetchCrntCityName(result.name);
    },[name])
    const doChange = (event) =>
    {
        setCrntCity(event.target.value);
    }
    const searchData = () =>{
        setName(crntCity);
        setFlag(false);
    }
    return(
        <>
            <div className="Body_wrapper">
                <div className="card_wrapper">
                    <div className="card">
                        <div className="card_header">
                            <div className="search_bar">
                                <input 
                                    type="search"
                                    placeholder="Enter City Name"
                                    onChange={doChange}
                                    value = {crntCity}
                                />
                                {/* {flag ? (<button onClick= {searchData}>search</button>)
                                :(<button onClick={()=> {
                                    setCrntCity("");
                                    setFlag(true);
                                }}> Clear </button>)} */}
                                <button>{flag? "Search" : "Clear" }</button>
                            </div>
                        </div>
                        {!crntTemp? (
                            <p className="data-exception">No data Found</p>
                        ):(
                            <>
                            <div className="card_main">
                            <div className="city_info">
                                <div className="cities"> 
                                    <i className="fa-solid fa-location-dot"></i>
                                    <h1> {fetchCityName} </h1></div>
                                <div className="temp"><h1> {crntTemp.temp}* cel </h1></div>
                            </div>
                            <div className="min-max_temp">
                                <h3> min-temp : {crntTemp.temp_min}* cel</h3>
                                <h3>||</h3>
                                <h3>max-temp :{crntTemp.temp_max}* cel</h3>
                            </div>
                            </div>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </>
    )
}

export default WeatherM

